import logo from './logo.svg';
import './components/Component/Comments';
import './components/Component/CommentsList';
import './App.scss';
// import './assets/Images';


export default function App () {
  const customerComments =  [
    {firstName: "Michael", lastName: "Lyons", comment: "They BLEW the ROOF off at their last event, once everyone started figuring out they were going. This is still simply the greatest opening of an event I have EVER witnessed."},
    {firstName: "Gary", lastName: "Wong", comment: "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He's so talented! I wish I can ride like hin one day so I can really enjoy myself!"},
    {firstName: "Theodore", lastName: "Duncan", comment: "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He's definitly my favorite ever!" },
  ];

  return (
    <div className = "App">

      <div className="video__main">

      </div>
      <div className= "description">

      </div>
      <div className="videos">

      </div>

       <div className="comment__section">
       <img className ="Mohan__image" src= {("./assets/Images/Moham-muruge.jpg")} alt={'Mohan Muruge'}></img>
      <section>
        <Comment commentInformation ={customerComments[0]}></Comment>
        <Comment commentInformation ={customerComments[1]}></Comment>
        <Comment commentInformation ={customerComments[2]}></Comment>
      </section>
      </div>

    </div>
  );
}

function Comment (props) {
  console.log(props);

  return (
    <div class="heading">
      <img className ="Mohan__image" src= {("./assets/Images/Moham-muruge.jpg")} alt={'Mohan Muruge'}></img>
      <h2>{props.commentInformation.firstName}</h2>
      <h2>{props.commentInformation.lastName}</h2>
      <p>{props.commentInformation.comment}</p>
    </div>
  );
}
